INSERT INTO `b_article` VALUES (5, 7, 17, 'gateway之动态路由', '\r\nhttp://img.shiyit.com/1639990056972.jpg', '项目需求要根据一个请求动态生成gateway的路由，从而提供访问', '<h4><a id=\"DynamicRouteServiceImpl_ApplicationEventPublisherAware_1\"></a>创建DynamicRouteServiceImpl文件 并实现ApplicationEventPublisherAware。</h4>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-meta\">@Slf4j</span>\n<span class=\"hljs-meta\">@Service</span>\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">class</span> <span class=\"hljs-title class_\">DynamicRouteServiceImpl</span> <span class=\"hljs-keyword\">implements</span> <span class=\"hljs-title class_\">ApplicationEventPublisherAware</span> {\n\n    <span class=\"hljs-meta\">@Autowired</span>\n    <span class=\"hljs-keyword\">private</span> RouteDefinitionWriter routeDefinitionWriter;\n\n    <span class=\"hljs-keyword\">private</span> ApplicationEventPublisher publisher;\n\n    <span class=\"hljs-comment\">/**\n     * 添加路由实体类\n     *\n     * <span class=\"hljs-doctag\">@param</span> definition\n     * <span class=\"hljs-doctag\">@return</span>\n     */</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-type\">boolean</span> <span class=\"hljs-title function_\">add</span><span class=\"hljs-params\">(RouteDefinition definition)</span> {\n        routeDefinitionWriter.save(Mono.just(definition)).subscribe();\n        <span class=\"hljs-built_in\">this</span>.publisher.publishEvent(<span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">RefreshRoutesEvent</span>(<span class=\"hljs-built_in\">this</span>));\n        <span class=\"hljs-keyword\">return</span> <span class=\"hljs-literal\">true</span>;\n    }\n\n    <span class=\"hljs-comment\">/**\n     * <span class=\"hljs-doctag\">@param</span> definition 路由实体类\n     * <span class=\"hljs-doctag\">@return</span>\n     */</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-type\">boolean</span> <span class=\"hljs-title function_\">update</span><span class=\"hljs-params\">(RouteDefinition definition)</span> {\n        <span class=\"hljs-keyword\">try</span> {\n            routeDefinitionWriter.delete(Mono.just(definition.getId())).subscribe();\n        } <span class=\"hljs-keyword\">catch</span> (Exception e) {\n            log.error(<span class=\"hljs-string\">&quot;update 失败。没有找到对应的路由ID :{}&quot;</span>, definition.getId());\n        }\n        routeDefinitionWriter.save(Mono.just(definition)).subscribe();\n        <span class=\"hljs-built_in\">this</span>.publisher.publishEvent(<span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">RefreshRoutesEvent</span>(<span class=\"hljs-built_in\">this</span>));\n        <span class=\"hljs-keyword\">return</span> <span class=\"hljs-literal\">true</span>;\n    }\n\n    <span class=\"hljs-comment\">/**\n     * serviceId\n     *\n     * <span class=\"hljs-doctag\">@param</span> id\n     * <span class=\"hljs-doctag\">@return</span>\n     */</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-type\">boolean</span> <span class=\"hljs-title function_\">del</span><span class=\"hljs-params\">(String id)</span> {\n        routeDefinitionWriter.delete(Mono.just(id)).subscribe();\n        <span class=\"hljs-built_in\">this</span>.publisher.publishEvent(<span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">RefreshRoutesEvent</span>(<span class=\"hljs-built_in\">this</span>));\n        <span class=\"hljs-keyword\">return</span> <span class=\"hljs-literal\">true</span>;\n    }\n\n    <span class=\"hljs-meta\">@Override</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title function_\">setApplicationEventPublisher</span><span class=\"hljs-params\">(ApplicationEventPublisher applicationEventPublisher)</span> {\n        <span class=\"hljs-built_in\">this</span>.publisher = applicationEventPublisher;\n    }\n}\n\n</code></div></pre>\n<h4><a id=\"RefreshRouteServiceApplicationEventPublisherAware_59\"></a>新建RefreshRouteService文件并实现ApplicationEventPublisherAware</h4>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-meta\">@Component</span>\n<span class=\"hljs-meta\">@Slf4j</span>\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">class</span> <span class=\"hljs-title class_\">RefreshRouteService</span> <span class=\"hljs-keyword\">implements</span> <span class=\"hljs-title class_\">ApplicationEventPublisherAware</span> {\n\n    <span class=\"hljs-keyword\">private</span> ApplicationEventPublisher publisher;\n\n    <span class=\"hljs-meta\">@Override</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title function_\">setApplicationEventPublisher</span><span class=\"hljs-params\">(ApplicationEventPublisher publisher)</span> {\n        <span class=\"hljs-built_in\">this</span>.publisher = publisher;\n    }\n\n    <span class=\"hljs-comment\">/**\n     * 刷新路由表\n     */</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title function_\">refreshRoutes</span><span class=\"hljs-params\">()</span> {\n        publisher.publishEvent(<span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">RefreshRoutesEvent</span>(<span class=\"hljs-built_in\">this</span>));\n    }\n}\n\n</code></div></pre>\n<h4><a id=\"GatewayRoutesController_82\"></a>新建GatewayRoutesController</h4>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-meta\">@RestController</span>\n<span class=\"hljs-meta\">@RequestMapping(&quot;/gateway&quot;)</span>\n\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">class</span> <span class=\"hljs-title class_\">GatewayRoutesController</span> {\n\n    <span class=\"hljs-meta\">@Autowired</span>\n    <span class=\"hljs-keyword\">private</span> RefreshRouteService refreshRouteService;\n\n    <span class=\"hljs-meta\">@Autowired</span>\n    <span class=\"hljs-keyword\">private</span> DynamicRouteServiceImpl dynamicRouteService;\n\n    <span class=\"hljs-meta\">@GetMapping(&quot;/refreshRoutes&quot;)</span>\n    <span class=\"hljs-keyword\">public</span> CommonResult <span class=\"hljs-title function_\">refreshRoutes</span><span class=\"hljs-params\">()</span> {\n        refreshRouteService.refreshRoutes();\n        <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;成功&quot;</span>).build();\n    }\n\n    <span class=\"hljs-comment\">/**\n     * <span class=\"hljs-doctag\">@param</span>\n     * <span class=\"hljs-doctag\">@return</span>\n     */</span>\n    <span class=\"hljs-meta\">@RequestMapping(value = &quot;routes/add&quot;, method = RequestMethod.POST)</span>\n    <span class=\"hljs-keyword\">public</span> CommonResult <span class=\"hljs-title function_\">add</span><span class=\"hljs-params\">(<span class=\"hljs-meta\">@RequestBody</span> RouteDefinition routeDefinition)</span> {\n        <span class=\"hljs-type\">boolean</span> <span class=\"hljs-variable\">flag</span> <span class=\"hljs-operator\">=</span> dynamicRouteService.add(routeDefinition);\n        <span class=\"hljs-keyword\">if</span> (flag) <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;成功&quot;</span>).build();\n        <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;失败&quot;</span>).build();\n    }\n\n    <span class=\"hljs-comment\">/**\n     * <span class=\"hljs-doctag\">@param</span> definition\n     * <span class=\"hljs-doctag\">@return</span>\n     */</span>\n    <span class=\"hljs-meta\">@RequestMapping(value = &quot;routes/update&quot;, method = RequestMethod.POST)</span>\n    <span class=\"hljs-keyword\">public</span> CommonResult <span class=\"hljs-title function_\">update</span><span class=\"hljs-params\">(<span class=\"hljs-meta\">@RequestBody</span> RouteDefinition definition)</span> {\n        <span class=\"hljs-type\">boolean</span> <span class=\"hljs-variable\">flag</span> <span class=\"hljs-operator\">=</span> dynamicRouteService.update(definition);\n        <span class=\"hljs-keyword\">if</span> (flag) <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;成功&quot;</span>).build();\n        <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;失败&quot;</span>).build();\n    }\n\n    <span class=\"hljs-comment\">/**\n     * <span class=\"hljs-doctag\">@param</span> serviceId\n     * <span class=\"hljs-doctag\">@return</span>\n     */</span>\n    <span class=\"hljs-meta\">@RequestMapping(value = &quot;routes/del&quot;, method = RequestMethod.POST)</span>\n    <span class=\"hljs-keyword\">public</span> CommonResult <span class=\"hljs-title function_\">update</span><span class=\"hljs-params\">(<span class=\"hljs-meta\">@RequestParam(&quot;serviceId&quot;)</span> String serviceId)</span> {\n        <span class=\"hljs-type\">boolean</span> <span class=\"hljs-variable\">flag</span> <span class=\"hljs-operator\">=</span> dynamicRouteService.del(serviceId);\n        <span class=\"hljs-keyword\">if</span> (flag) <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;成功&quot;</span>).build();\n        <span class=\"hljs-keyword\">return</span> CommonResult.builder().message(<span class=\"hljs-string\">&quot;失败&quot;</span>).build();\n    }\n\n}\n\n</code></div></pre>\n<h4><a id=\"routesadd__137\"></a>当需要添加路由时只需执行routes/add接口 传入相对应的参数格式即可</h4>\n', '\n#### 创建DynamicRouteServiceImpl文件 并实现ApplicationEventPublisherAware。\n```java\n@Slf4j\n@Service\npublic class DynamicRouteServiceImpl implements ApplicationEventPublisherAware {\n\n    @Autowired\n    private RouteDefinitionWriter routeDefinitionWriter;\n\n    private ApplicationEventPublisher publisher;\n\n    /**\n     * 添加路由实体类\n     *\n     * @param definition\n     * @return\n     */\n    public boolean add(RouteDefinition definition) {\n        routeDefinitionWriter.save(Mono.just(definition)).subscribe();\n        this.publisher.publishEvent(new RefreshRoutesEvent(this));\n        return true;\n    }\n\n    /**\n     * @param definition 路由实体类\n     * @return\n     */\n    public boolean update(RouteDefinition definition) {\n        try {\n            routeDefinitionWriter.delete(Mono.just(definition.getId())).subscribe();\n        } catch (Exception e) {\n            log.error(\"update 失败。没有找到对应的路由ID :{}\", definition.getId());\n        }\n        routeDefinitionWriter.save(Mono.just(definition)).subscribe();\n        this.publisher.publishEvent(new RefreshRoutesEvent(this));\n        return true;\n    }\n\n    /**\n     * serviceId\n     *\n     * @param id\n     * @return\n     */\n    public boolean del(String id) {\n        routeDefinitionWriter.delete(Mono.just(id)).subscribe();\n        this.publisher.publishEvent(new RefreshRoutesEvent(this));\n        return true;\n    }\n\n    @Override\n    public void setApplicationEventPublisher(ApplicationEventPublisher applicationEventPublisher) {\n        this.publisher = applicationEventPublisher;\n    }\n}\n\n```\n\n#### 新建RefreshRouteService文件并实现ApplicationEventPublisherAware\n\n```java\n@Component\n@Slf4j\npublic class RefreshRouteService implements ApplicationEventPublisherAware {\n\n    private ApplicationEventPublisher publisher;\n\n    @Override\n    public void setApplicationEventPublisher(ApplicationEventPublisher publisher) {\n        this.publisher = publisher;\n    }\n\n    /**\n     * 刷新路由表\n     */\n    public void refreshRoutes() {\n        publisher.publishEvent(new RefreshRoutesEvent(this));\n    }\n}\n\n```\n#### 新建GatewayRoutesController\n```java\n@RestController\n@RequestMapping(\"/gateway\")\n\npublic class GatewayRoutesController {\n\n    @Autowired\n    private RefreshRouteService refreshRouteService;\n\n    @Autowired\n    private DynamicRouteServiceImpl dynamicRouteService;\n\n    @GetMapping(\"/refreshRoutes\")\n    public CommonResult refreshRoutes() {\n        refreshRouteService.refreshRoutes();\n        return CommonResult.builder().message(\"成功\").build();\n    }\n\n    /**\n     * @param\n     * @return\n     */\n    @RequestMapping(value = \"routes/add\", method = RequestMethod.POST)\n    public CommonResult add(@RequestBody RouteDefinition routeDefinition) {\n        boolean flag = dynamicRouteService.add(routeDefinition);\n        if (flag) return CommonResult.builder().message(\"成功\").build();\n        return CommonResult.builder().message(\"失败\").build();\n    }\n\n    /**\n     * @param definition\n     * @return\n     */\n    @RequestMapping(value = \"routes/update\", method = RequestMethod.POST)\n    public CommonResult update(@RequestBody RouteDefinition definition) {\n        boolean flag = dynamicRouteService.update(definition);\n        if (flag) return CommonResult.builder().message(\"成功\").build();\n        return CommonResult.builder().message(\"失败\").build();\n    }\n\n    /**\n     * @param serviceId\n     * @return\n     */\n    @RequestMapping(value = \"routes/del\", method = RequestMethod.POST)\n    public CommonResult update(@RequestParam(\"serviceId\") String serviceId) {\n        boolean flag = dynamicRouteService.del(serviceId);\n        if (flag) return CommonResult.builder().message(\"成功\").build();\n        return CommonResult.builder().message(\"失败\").build();\n    }\n\n}\n\n```\n#### 当需要添加路由时只需执行routes/add接口 传入相对应的参数格式即可\n', 0, 0, 1, 1, NULL, 838, '', '2021-09-01 10:35:13', 'gateway,动态路由,springboot,springcloud', '2023-06-05 04:00:00', 0);
INSERT INTO `b_article` VALUES (9, 1, 18, 'springboot操作elasticsearch实现基本增删改查', '\r\nhttp://img.shiyit.com/1639990013089.jpg', '基于elasticsearch7.14版本使用官方的restHighLevelClient实现操作', '<h4><a id=\"1_0\"></a>1、添加依赖</h4>\n<pre><div class=\"hljs\"><code class=\"lang-java\">&lt;dependency&gt;\n            &lt;groupId&gt;org.elasticsearch.client&lt;/groupId&gt;\n            &lt;artifactId&gt;elasticsearch-rest-client&lt;/artifactId&gt;\n            &lt;version&gt;<span class=\"hljs-number\">7.6</span><span class=\"hljs-number\">.2</span>&lt;/version&gt;\n        &lt;/dependency&gt;\n        &lt;dependency&gt;\n            &lt;groupId&gt;org.elasticsearch.client&lt;/groupId&gt;\n            &lt;artifactId&gt;elasticsearch-rest-high-level-client&lt;/artifactId&gt;\n            &lt;version&gt;<span class=\"hljs-number\">7.14</span><span class=\"hljs-number\">.1</span>&lt;/version&gt;\n            &lt;exclusions&gt;\n                &lt;exclusion&gt;\n                    &lt;artifactId&gt;elasticsearch-rest-client&lt;/artifactId&gt;\n                    &lt;groupId&gt;org.elasticsearch.client&lt;/groupId&gt;\n                &lt;/exclusion&gt;\n            &lt;/exclusions&gt;\n        &lt;/dependency&gt;\n        &lt;dependency&gt;\n            &lt;groupId&gt;org.elasticsearch&lt;/groupId&gt;\n            &lt;artifactId&gt;elasticsearch&lt;/artifactId&gt;\n            &lt;version&gt;<span class=\"hljs-number\">7.14</span><span class=\"hljs-number\">.1</span>&lt;/version&gt;\n        &lt;/dependency&gt;\n</code></div></pre>\n<h4><a id=\"2ElasticsearchConfiges_24\"></a>2、新建ElasticsearchConfig文件配置es</h4>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-meta\">@Configuration</span>\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">class</span> <span class=\"hljs-title class_\">ElasticsearchConfig</span> {\n\n    <span class=\"hljs-meta\">@Value(&quot;${searchServer.elasticsearch.hostlist}&quot;)</span>\n    <span class=\"hljs-keyword\">private</span> String hostlist;\n\n    <span class=\"hljs-meta\">@Bean</span>\n    <span class=\"hljs-keyword\">public</span> RestHighLevelClient <span class=\"hljs-title function_\">restHighLevelClient</span><span class=\"hljs-params\">()</span>{\n        <span class=\"hljs-comment\">//解析hostlist配置信息</span>\n        String[] split = hostlist.split(<span class=\"hljs-string\">&quot;,&quot;</span>);\n        <span class=\"hljs-comment\">//创建HttpHost数组，其中存放es主机和端口的配置信息</span>\n        HttpHost[] httpHostArray = <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">HttpHost</span>[split.length];\n        <span class=\"hljs-keyword\">for</span>(<span class=\"hljs-type\">int</span> i=<span class=\"hljs-number\">0</span>;i&lt;split.length;i++){\n            <span class=\"hljs-type\">String</span> <span class=\"hljs-variable\">item</span> <span class=\"hljs-operator\">=</span> split[i];\n            httpHostArray[i] = <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">HttpHost</span>(item.split(<span class=\"hljs-string\">&quot;:&quot;</span>)[<span class=\"hljs-number\">0</span>], Integer.parseInt(item.split(<span class=\"hljs-string\">&quot;:&quot;</span>)[<span class=\"hljs-number\">1</span>]), <span class=\"hljs-string\">&quot;http&quot;</span>);\n        }\n        <span class=\"hljs-comment\">//创建RestHighLevelClient客户端</span>\n        <span class=\"hljs-keyword\">return</span> <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">RestHighLevelClient</span>(RestClient.builder(httpHostArray));\n    }\n\n    <span class=\"hljs-comment\">//项目主要使用RestHighLevelClient，对于低级的客户端暂时不用</span>\n    <span class=\"hljs-meta\">@Bean</span>\n    <span class=\"hljs-keyword\">public</span> RestClient <span class=\"hljs-title function_\">restClient</span><span class=\"hljs-params\">()</span>{\n        <span class=\"hljs-comment\">//解析hostlist配置信息</span>\n        String[] split = hostlist.split(<span class=\"hljs-string\">&quot;,&quot;</span>);\n        <span class=\"hljs-comment\">//创建HttpHost数组，其中存放es主机和端口的配置信息</span>\n        HttpHost[] httpHostArray = <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">HttpHost</span>[split.length];\n        <span class=\"hljs-keyword\">for</span>(<span class=\"hljs-type\">int</span> i=<span class=\"hljs-number\">0</span>;i&lt;split.length;i++){\n            <span class=\"hljs-type\">String</span> <span class=\"hljs-variable\">item</span> <span class=\"hljs-operator\">=</span> split[i];\n            httpHostArray[i] = <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">HttpHost</span>(item.split(<span class=\"hljs-string\">&quot;:&quot;</span>)[<span class=\"hljs-number\">0</span>], Integer.parseInt(item.split(<span class=\"hljs-string\">&quot;:&quot;</span>)[<span class=\"hljs-number\">1</span>]), <span class=\"hljs-string\">&quot;http&quot;</span>);\n        }\n        <span class=\"hljs-keyword\">return</span> RestClient.builder(httpHostArray).build();\n    }\n}\n</code></div></pre>\n<p>yml文件配置</p>\n<pre><div class=\"hljs\"><code class=\"lang-yml\"><span class=\"hljs-attr\">searchServer:</span>\n  <span class=\"hljs-attr\">elasticsearch:</span>\n    <span class=\"hljs-attr\">hostlist:</span> <span class=\"hljs-string\">${eshostlist:localhost:9200}</span> <span class=\"hljs-comment\">#多个结点中间用逗号分隔</span>\n</code></div></pre>\n<h4><a id=\"3httplocalhost9200_67\"></a>3、配置完后启动项目访问http://localhost:9200/</h4>\n<p>接下来就是操作es了<br />\n1、分页查询</p>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-keyword\">public</span> Map&lt;String,Object&gt; <span class=\"hljs-title function_\">selectEventPage</span><span class=\"hljs-params\">(String index,<span class=\"hljs-type\">int</span> from,<span class=\"hljs-type\">int</span> size,String value,Integer id)</span> {\n        Map&lt;String,Object&gt; map = <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">HashMap</span>&lt;&gt;();\n        <span class=\"hljs-comment\">//设置ES索引和类型</span>\n        <span class=\"hljs-type\">SearchRequest</span> <span class=\"hljs-variable\">searchRequest</span> <span class=\"hljs-operator\">=</span> <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">SearchRequest</span>(index);\n\n        <span class=\"hljs-comment\">/**\n         * 分页参数\n         * 第一页为0\n         */</span>\n        <span class=\"hljs-type\">SearchSourceBuilder</span> <span class=\"hljs-variable\">searchSourceBuilder</span> <span class=\"hljs-operator\">=</span> <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">SearchSourceBuilder</span>();\n        searchSourceBuilder.from(from*size);\n        searchSourceBuilder.size(size);\n\n        <span class=\"hljs-comment\">//最大的查询条件</span>\n        <span class=\"hljs-type\">BoolQueryBuilder</span> <span class=\"hljs-variable\">boolQueryBuilder</span> <span class=\"hljs-operator\">=</span> QueryBuilders.boolQuery();\n        boolQueryBuilder.must(QueryBuilders.termQuery(<span class=\"hljs-string\">&quot;deleteFlag&quot;</span>,<span class=\"hljs-number\">0</span>));\n\n        <span class=\"hljs-comment\">/**\n         * 查询条件\n         */</span>\n        <span class=\"hljs-keyword\">if</span> (StringUtils.isNotBlank(value)){\n            boolQueryBuilder.must(QueryBuilders.multiMatchQuery(value,<span class=\"hljs-string\">&quot;acticleSimple&quot;</span>,<span class=\"hljs-string\">&quot;articleName&quot;</span> ).\n                    analyzer(<span class=\"hljs-string\">&quot;ik_max_word&quot;</span>).operator(Operator.OR));\n\n        }\n        <span class=\"hljs-keyword\">if</span> (<span class=\"hljs-literal\">null</span> != id) boolQueryBuilder.must(QueryBuilders.termQuery(<span class=\"hljs-string\">&quot;tagId&quot;</span>,id));\n\n        <span class=\"hljs-comment\">//设置高亮字段</span>\n        <span class=\"hljs-type\">HighlightBuilder</span> <span class=\"hljs-variable\">highlightBuilder</span> <span class=\"hljs-operator\">=</span> <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">HighlightBuilder</span>();\n        highlightBuilder.preTags(<span class=\"hljs-string\">&quot;&lt;span style=\\&quot;color:red\\&quot;&gt;&quot;</span>);\n        highlightBuilder.postTags(<span class=\"hljs-string\">&quot;&lt;/span&gt;&quot;</span>);\n        <span class=\"hljs-comment\">//高亮字段</span>\n        highlightBuilder.field(<span class=\"hljs-string\">&quot;articleName&quot;</span>).field(<span class=\"hljs-string\">&quot;acticleSimple&quot;</span>);\n        searchSourceBuilder.highlighter(highlightBuilder);\n        <span class=\"hljs-comment\">//将searchSourceBuilder放入searchRequest中</span>\n        searchRequest.source(searchSourceBuilder);\n        <span class=\"hljs-comment\">//搜索  排序</span>\n        searchSourceBuilder.query(boolQueryBuilder).sort(<span class=\"hljs-string\">&quot;isStick&quot;</span>, SortOrder.ASC).sort(<span class=\"hljs-string\">&quot;createTime&quot;</span>,SortOrder.ASC);\n        <span class=\"hljs-keyword\">try</span> {\n            <span class=\"hljs-type\">SearchResponse</span> <span class=\"hljs-variable\">searchResponse</span> <span class=\"hljs-operator\">=</span> restHighLevelClient.search(searchRequest,RequestOptions.DEFAULT);\n            <span class=\"hljs-comment\">//搜索结果</span>\n            <span class=\"hljs-type\">SearchHits</span> <span class=\"hljs-variable\">searchHits</span> <span class=\"hljs-operator\">=</span> searchResponse.getHits();\n            <span class=\"hljs-comment\">//计算总数量</span>\n            <span class=\"hljs-type\">long</span> <span class=\"hljs-variable\">totalCount</span> <span class=\"hljs-operator\">=</span> searchResponse.getHits().getTotalHits().value;\n            map.put(<span class=\"hljs-string\">&quot;totalCount&quot;</span>,totalCount);\n            <span class=\"hljs-comment\">//得到总页数</span>\n            <span class=\"hljs-type\">int</span> <span class=\"hljs-variable\">page</span> <span class=\"hljs-operator\">=</span> (<span class=\"hljs-type\">int</span>) Math.ceil((<span class=\"hljs-type\">float</span>) totalCount / size);\n            map.put(<span class=\"hljs-string\">&quot;totalPage&quot;</span>,page);\n            map.put(<span class=\"hljs-string\">&quot;current&quot;</span>,from);\n            List&lt;BlogArticle&gt; eventEsResultList = <span class=\"hljs-keyword\">new</span> <span class=\"hljs-title class_\">ArrayList</span>&lt;&gt;();\n            <span class=\"hljs-keyword\">for</span> (SearchHit searchHit : searchHits) {\n                <span class=\"hljs-type\">Map</span> <span class=\"hljs-variable\">eventMap</span> <span class=\"hljs-operator\">=</span> searchHit.getSourceAsMap();\n                <span class=\"hljs-type\">BlogArticle</span> <span class=\"hljs-variable\">article</span> <span class=\"hljs-operator\">=</span> JSON.parseObject(JSON.toJSONString(eventMap), BlogArticle.class);\n                Map&lt;String, HighlightField&gt; highlightFields = searchHit.getHighlightFields();\n                <span class=\"hljs-comment\">//高亮字段</span>\n                <span class=\"hljs-type\">HighlightField</span> <span class=\"hljs-variable\">highlight_articleName</span> <span class=\"hljs-operator\">=</span> highlightFields.get(<span class=\"hljs-string\">&quot;articleName&quot;</span>);\n                <span class=\"hljs-keyword\">if</span>(highlight_articleName!=<span class=\"hljs-literal\">null</span>){\n                    <span class=\"hljs-comment\">// 为title串值增加自定义的高亮标签</span>\n                    Text[] titleTexts = highlight_articleName.fragments();\n                    <span class=\"hljs-type\">String</span> <span class=\"hljs-variable\">articleName</span> <span class=\"hljs-operator\">=</span> <span class=\"hljs-string\">&quot;&quot;</span>;\n                    <span class=\"hljs-keyword\">for</span> (Text text : titleTexts) {\n                        articleName += text;\n                    }\n                    <span class=\"hljs-comment\">//将追加了高亮标签的串值重新填充到对应的对象</span>\n                    article.setArticleName(articleName);\n                }\n\n                <span class=\"hljs-type\">HighlightField</span> <span class=\"hljs-variable\">highlight_acticleSimple</span> <span class=\"hljs-operator\">=</span> highlightFields.get(<span class=\"hljs-string\">&quot;acticleSimple&quot;</span>);\n                <span class=\"hljs-keyword\">if</span>(highlight_acticleSimple!=<span class=\"hljs-literal\">null</span>){\n                    Text[] titleTexts = highlight_acticleSimple.fragments();\n                    <span class=\"hljs-comment\">// 为title串值增加自定义的高亮标签</span>\n                    <span class=\"hljs-type\">String</span> <span class=\"hljs-variable\">acticleSimple</span> <span class=\"hljs-operator\">=</span> <span class=\"hljs-string\">&quot;&quot;</span>;\n                    <span class=\"hljs-keyword\">for</span> (Text text : titleTexts) {\n                        acticleSimple += text;\n                    }\n                    <span class=\"hljs-comment\">//将追加了高亮标签的串值重新填充到对应的对象</span>\n                    article.setActicleSimple(acticleSimple);\n                }\n                <span class=\"hljs-keyword\">try</span> {\n                    eventEsResultList.add(article);\n                } <span class=\"hljs-keyword\">catch</span> (Exception e) {\n                    e.printStackTrace();\n                }\n            }\n            map.put(<span class=\"hljs-string\">&quot;data&quot;</span>,eventEsResultList);\n        } <span class=\"hljs-keyword\">catch</span> (IOException e) {\n            e.printStackTrace();\n        }\n        <span class=\"hljs-keyword\">return</span> map;\n    }\n</code></div></pre>\n<hr />\n', '#### 1、添加依赖\n```java\n<dependency>\n            <groupId>org.elasticsearch.client</groupId>\n            <artifactId>elasticsearch-rest-client</artifactId>\n            <version>7.6.2</version>\n        </dependency>\n        <dependency>\n            <groupId>org.elasticsearch.client</groupId>\n            <artifactId>elasticsearch-rest-high-level-client</artifactId>\n            <version>7.14.1</version>\n            <exclusions>\n                <exclusion>\n                    <artifactId>elasticsearch-rest-client</artifactId>\n                    <groupId>org.elasticsearch.client</groupId>\n                </exclusion>\n            </exclusions>\n        </dependency>\n        <dependency>\n            <groupId>org.elasticsearch</groupId>\n            <artifactId>elasticsearch</artifactId>\n            <version>7.14.1</version>\n        </dependency>\n```\n#### 2、新建ElasticsearchConfig文件配置es\n```java\n@Configuration\npublic class ElasticsearchConfig {\n\n    @Value(\"${searchServer.elasticsearch.hostlist}\")\n    private String hostlist;\n\n    @Bean\n    public RestHighLevelClient restHighLevelClient(){\n        //解析hostlist配置信息\n        String[] split = hostlist.split(\",\");\n        //创建HttpHost数组，其中存放es主机和端口的配置信息\n        HttpHost[] httpHostArray = new HttpHost[split.length];\n        for(int i=0;i<split.length;i++){\n            String item = split[i];\n            httpHostArray[i] = new HttpHost(item.split(\":\")[0], Integer.parseInt(item.split(\":\")[1]), \"http\");\n        }\n        //创建RestHighLevelClient客户端\n        return new RestHighLevelClient(RestClient.builder(httpHostArray));\n    }\n\n    //项目主要使用RestHighLevelClient，对于低级的客户端暂时不用\n    @Bean\n    public RestClient restClient(){\n        //解析hostlist配置信息\n        String[] split = hostlist.split(\",\");\n        //创建HttpHost数组，其中存放es主机和端口的配置信息\n        HttpHost[] httpHostArray = new HttpHost[split.length];\n        for(int i=0;i<split.length;i++){\n            String item = split[i];\n            httpHostArray[i] = new HttpHost(item.split(\":\")[0], Integer.parseInt(item.split(\":\")[1]), \"http\");\n        }\n        return RestClient.builder(httpHostArray).build();\n    }\n}\n```\nyml文件配置\n```yml\nsearchServer:\n  elasticsearch:\n    hostlist: ${eshostlist:localhost:9200} #多个结点中间用逗号分隔\n```\n#### 3、配置完后启动项目访问http://localhost:9200/\n\n接下来就是操作es了\n1、分页查询\n```java\npublic Map<String,Object> selectEventPage(String index,int from,int size,String value,Integer id) {\n        Map<String,Object> map = new HashMap<>();\n        //设置ES索引和类型\n        SearchRequest searchRequest = new SearchRequest(index);\n\n        /**\n         * 分页参数\n         * 第一页为0\n         */\n        SearchSourceBuilder searchSourceBuilder = new SearchSourceBuilder();\n        searchSourceBuilder.from(from*size);\n        searchSourceBuilder.size(size);\n\n        //最大的查询条件\n        BoolQueryBuilder boolQueryBuilder = QueryBuilders.boolQuery();\n        boolQueryBuilder.must(QueryBuilders.termQuery(\"deleteFlag\",0));\n\n        /**\n         * 查询条件\n         */\n        if (StringUtils.isNotBlank(value)){\n            boolQueryBuilder.must(QueryBuilders.multiMatchQuery(value,\"acticleSimple\",\"articleName\" ).\n                    analyzer(\"ik_max_word\").operator(Operator.OR));\n\n        }\n        if (null != id) boolQueryBuilder.must(QueryBuilders.termQuery(\"tagId\",id));\n\n        //设置高亮字段\n        HighlightBuilder highlightBuilder = new HighlightBuilder();\n        highlightBuilder.preTags(\"<span style=\\\"color:red\\\">\");\n        highlightBuilder.postTags(\"</span>\");\n        //高亮字段\n        highlightBuilder.field(\"articleName\").field(\"acticleSimple\");\n        searchSourceBuilder.highlighter(highlightBuilder);\n        //将searchSourceBuilder放入searchRequest中\n        searchRequest.source(searchSourceBuilder);\n        //搜索  排序\n        searchSourceBuilder.query(boolQueryBuilder).sort(\"isStick\", SortOrder.ASC).sort(\"createTime\",SortOrder.ASC);\n        try {\n            SearchResponse searchResponse = restHighLevelClient.search(searchRequest,RequestOptions.DEFAULT);\n            //搜索结果\n            SearchHits searchHits = searchResponse.getHits();\n            //计算总数量\n            long totalCount = searchResponse.getHits().getTotalHits().value;\n            map.put(\"totalCount\",totalCount);\n            //得到总页数\n            int page = (int) Math.ceil((float) totalCount / size);\n            map.put(\"totalPage\",page);\n            map.put(\"current\",from);\n            List<BlogArticle> eventEsResultList = new ArrayList<>();\n            for (SearchHit searchHit : searchHits) {\n                Map eventMap = searchHit.getSourceAsMap();\n                BlogArticle article = JSON.parseObject(JSON.toJSONString(eventMap), BlogArticle.class);\n                Map<String, HighlightField> highlightFields = searchHit.getHighlightFields();\n                //高亮字段\n                HighlightField highlight_articleName = highlightFields.get(\"articleName\");\n                if(highlight_articleName!=null){\n                    // 为title串值增加自定义的高亮标签\n                    Text[] titleTexts = highlight_articleName.fragments();\n                    String articleName = \"\";\n                    for (Text text : titleTexts) {\n                        articleName += text;\n                    }\n                    //将追加了高亮标签的串值重新填充到对应的对象\n                    article.setArticleName(articleName);\n                }\n\n                HighlightField highlight_acticleSimple = highlightFields.get(\"acticleSimple\");\n                if(highlight_acticleSimple!=null){\n                    Text[] titleTexts = highlight_acticleSimple.fragments();\n                    // 为title串值增加自定义的高亮标签\n                    String acticleSimple = \"\";\n                    for (Text text : titleTexts) {\n                        acticleSimple += text;\n                    }\n                    //将追加了高亮标签的串值重新填充到对应的对象\n                    article.setActicleSimple(acticleSimple);\n                }\n                try {\n                    eventEsResultList.add(article);\n                } catch (Exception e) {\n                    e.printStackTrace();\n                }\n            }\n            map.put(\"data\",eventEsResultList);\n        } catch (IOException e) {\n            e.printStackTrace();\n        }\n        return map;\n    }\n```\n----------------------------------------------------\n', 1, 0, 1, 1, NULL, 952, '', '2021-09-09 15:03:07', 'springboot,elasticsearch,sprinbcloud,java,restHighLevelClient', '2023-06-05 04:00:00', 0);
INSERT INTO `b_article` VALUES (14, 101, 12, '关于博客', 'https://tva2.sinaimg.cn/large/9bd9b167ly1g2rmpwaxkkj21hc0u0e81.jpg', '关于博客的一些介绍。', '<blockquote>\n<p>本文主要介绍博客用到的技术和一些功能详情</p>\n</blockquote>\n<h4><a id=\"1_1\"></a>1、博客使用到的技术</h4>\n<ol>\n<li>后端：springboot，mysql，es搜索引擎，以及redis</li>\n<li>前端：vue</li>\n</ol>\n<h4><a id=\"2_5\"></a>2、首页登录</h4>\n<p>本博客支持<a href=\"https://connect.qq.com/index.html\" target=\"_blank\">QQ</a>、<a href=\"https://gitee.com/api/v5/oauth_doc#/\" target=\"_blank\">gitee</a>、<a href=\"https://open.weibo.com/?bottomnav=1&amp;wvr=6\" target=\"_blank\">微博</a>、邮箱登录</p>\n<h4><a id=\"3_7\"></a>3、图片的存储</h4>\n<p>项目中图片存储采用的是<a href=\"https://www.qiniu.com/products/kodo\" target=\"_blank\">七牛云</a>平台的对象存储，因为只有七牛云每个月会有免费的一个额度，其他的都是需要收费的。</p>\n<h4><a id=\"4_9\"></a>4、首页的搜索</h4>\n<p>首页的搜索采用的是es的搜索，eslinux启动的话会占用比较高的一个内存，所以博主介意如需要使用es搜索的购买服务器时资金允许的情况下尽可能购买内存比较多的服务器。然后项目搜索的话主要是围绕文章的标题和内容进行的分词搜索。</p>\n<blockquote>\n<p>上面的话都是一些首页的介绍，原作者只是提供了一个模板，并没有提供后台管理的一个功能，所以我就自己写了一个后台管理，主要借用的是比较火的一个<a href=\"https://gitee.com/panjiachen/vue-admin-template\" target=\"_blank\">vue-admin-template</a>项目模板来进行的二次开发，由于时间有限，所以后台管理开发的功能并不是很多，但是也能进行一个简单的博客管理了。</p>\n</blockquote>\n<h3><a id=\"_14\"></a>后台管理</h3>\n<blockquote>\n<p><a href=\"http://www.shiyit.com/admin\" target=\"_blank\">后台地址</a><br />\n演示账号：test,密码：test</p>\n</blockquote>\n<h4><a id=\"1_17\"></a>1、登录界面</h4>\n<p><img src=\"http://img.shiyit.com/1639989154384.jpg\" alt=\"login.jpg\" /></p>\n<h4><a id=\"2_19\"></a>2、后台首页</h4>\n<p><img src=\"http://img.shiyit.com/1639989162006.png\" alt=\"adminhome1.png\" /><br />\n<img src=\"http://img.shiyit.com/1639989170907.png\" alt=\"adminhome2.png\" /></p>\n<h4><a id=\"3_22\"></a>3、文章管理</h4>\n<ol>\n<li>列表页<br />\n<img src=\"http://img.shiyit.com/1639989186471.png\" alt=\"文章列表.png\" /></li>\n<li>文章添加<br />\n<img src=\"http://img.shiyit.com/1639989202706.png\" alt=\"文章添加.png\" /><br />\n编辑器使用的是mavon-editor编辑器，如不会使用的可以点击下面的链接查看<br />\n<a href=\"https://blog.csdn.net/qq_43681948/article/details/101531303\" target=\"_blank\">Vue markdown编辑器</a></li>\n</ol>\n<h4><a id=\"4_29\"></a>4、标签管理</h4>\n<p><img src=\"http://img.shiyit.com/1639989247680.png\" alt=\"标签列表.png\" /></p>\n<h4><a id=\"5_31\"></a>5、日志管理</h4>\n<ol>\n<li>针对用户访问以及管理员操作和异常信息都做了一个日志的记录</li>\n</ol>\n<p><img src=\"http://img.shiyit.com/1639989291127.png\" alt=\"日志管理.png\" /></p>\n<h4><a id=\"6_35\"></a>6、系统管理</h4>\n<p><img src=\"http://img.shiyit.com/1639989383160.png\" alt=\"系统配置.png\" /></p>\n<h4><a id=\"7_37\"></a>7、监控中心</h4>\n<ol>\n<li>监控中心主要有服务器的监控和定时任务，定时任务支持自动添加修改删除功能，改了时间无需修改代码<br />\n<img src=\"http://img.shiyit.com/1639989456867.png\" alt=\"定时任务.png\" /></li>\n</ol>\n<h4><a id=\"_40\"></a>结尾</h4>\n<p>上述只是项目的一些基础的功能图片，小伙伴们可以自行登录去查看，后台管理所看到的菜单功能都已实现，还在着手后续的一些功能开发。后台管理全由我自己根据模板改造而来，毕竟我只会业余的前端技术，所以有所缺陷也属正常。如有比较有意思的功能欢迎各位小伙伴给我留言，作者觉得有意思的话就会着手开发。如本项目小伙伴觉得可以，希望能够码云star一下，万分感谢！！</p>\n<blockquote>\n<p>码云地址：<a href=\"https://gitee.com/quequnlong/vue-admin-blog\" target=\"_blank\">点我进入</a></p>\n</blockquote>\n<h1><a id=\"bye_43\"></a>bye</h1>\n', '> 本文主要介绍博客用到的技术和一些功能详情\n#### 1、博客使用到的技术\n1. 后端：springboot，mysql，es搜索引擎，以及redis\n2. 前端：vue\n\n#### 2、首页登录\n本博客支持[QQ](https://connect.qq.com/index.html)、[gitee](https://gitee.com/api/v5/oauth_doc#/)、[微博](https://open.weibo.com/?bottomnav=1&wvr=6)、邮箱登录\n#### 3、图片的存储\n项目中图片存储采用的是[七牛云](https://www.qiniu.com/products/kodo)平台的对象存储，因为只有七牛云每个月会有免费的一个额度，其他的都是需要收费的。\n#### 4、首页的搜索\n首页的搜索采用的是es的搜索，eslinux启动的话会占用比较高的一个内存，所以博主介意如需要使用es搜索的购买服务器时资金允许的情况下尽可能购买内存比较多的服务器。然后项目搜索的话主要是围绕文章的标题和内容进行的分词搜索。\n\n> 上面的话都是一些首页的介绍，原作者只是提供了一个模板，并没有提供后台管理的一个功能，所以我就自己写了一个后台管理，主要借用的是比较火的一个[vue-admin-template](https://gitee.com/panjiachen/vue-admin-template)项目模板来进行的二次开发，由于时间有限，所以后台管理开发的功能并不是很多，但是也能进行一个简单的博客管理了。\n\n###  后台管理\n> [后台地址](http://www.shiyit.com/admin)\n演示账号：test,密码：test\n#### 1、登录界面\n![login.jpg](http://img.shiyit.com/1639989154384.jpg)\n#### 2、后台首页\n![adminhome1.png](http://img.shiyit.com/1639989162006.png)\n![adminhome2.png](http://img.shiyit.com/1639989170907.png)\n#### 3、文章管理\n1. 列表页\n![文章列表.png](http://img.shiyit.com/1639989186471.png)\n2. 文章添加\n![文章添加.png](http://img.shiyit.com/1639989202706.png)\n编辑器使用的是mavon-editor编辑器，如不会使用的可以点击下面的链接查看\n[Vue markdown编辑器](https://blog.csdn.net/qq_43681948/article/details/101531303)\n#### 4、标签管理\n![标签列表.png](http://img.shiyit.com/1639989247680.png)\n#### 5、日志管理\n1. 针对用户访问以及管理员操作和异常信息都做了一个日志的记录\n\n![日志管理.png](http://img.shiyit.com/1639989291127.png)\n#### 6、系统管理\n![系统配置.png](http://img.shiyit.com/1639989383160.png)\n#### 7、监控中心\n1. 监控中心主要有服务器的监控和定时任务，定时任务支持自动添加修改删除功能，改了时间无需修改代码\n![定时任务.png](http://img.shiyit.com/1639989456867.png)\n#### 结尾\n上述只是项目的一些基础的功能图片，小伙伴们可以自行登录去查看，后台管理所看到的菜单功能都已实现，还在着手后续的一些功能开发。后台管理全由我自己根据模板改造而来，毕竟我只会业余的前端技术，所以有所缺陷也属正常。如有比较有意思的功能欢迎各位小伙伴给我留言，作者觉得有意思的话就会着手开发。如本项目小伙伴觉得可以，希望能够码云star一下，万分感谢！！\n> 码云地址：[点我进入](https://gitee.com/quequnlong/vue-admin-blog)\n# bye\n\n', 0, 1, 1, 1, NULL, 1746, '', '2021-10-15 09:57:22', 'blog,拾壹博客,博客', '2023-06-05 04:00:00', 0);
INSERT INTO `b_article` VALUES (87, 7, 19, 'springboot使用webmagic抓取文章', 'https://i.picsum.photos/id/1006/3000/2000.jpg?hmac=x83pQQ7LW1UTo8HxBcIWuRIVeN_uCg0cG6keXvNvM8g', 'springboot使用webmagic抓取文章', '<h4><a id=\"_0\"></a>引入依赖</h4>\n<pre><div class=\"hljs\"><code class=\"lang-xml\">   <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">dependency</span>&gt;</span>\n            <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">groupId</span>&gt;</span>us.codecraft<span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">groupId</span>&gt;</span>\n            <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">artifactId</span>&gt;</span>webmagic-core<span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">artifactId</span>&gt;</span>\n            <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">version</span>&gt;</span>0.7.3<span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">version</span>&gt;</span>\n        <span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">dependency</span>&gt;</span>\n        <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">dependency</span>&gt;</span>\n            <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">groupId</span>&gt;</span>us.codecraft<span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">groupId</span>&gt;</span>\n            <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">artifactId</span>&gt;</span>webmagic-extension<span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">artifactId</span>&gt;</span>\n            <span class=\"hljs-tag\">&lt;<span class=\"hljs-name\">version</span>&gt;</span>0.7.3<span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">version</span>&gt;</span>\n        <span class=\"hljs-tag\">&lt;/<span class=\"hljs-name\">dependency</span>&gt;</span>\n</code></div></pre>\n<h4><a id=\"PageProcessor_13\"></a>创建抓取类实现PageProcessor</h4>\n<pre><div class=\"hljs\"><code class=\"lang-java\"><span class=\"hljs-comment\">/**\n * <span class=\"hljs-doctag\">@author</span> blue\n * <